mapper.select:
select * from(
	select rownum R, p.* from (
		select * from product where name like '%'||#{search}||'%'
	)p ) where R between #{startRow} and #{lastRow}

select * from(
	select rownum R, p.* from (
		select * from product where category1 like '%'||#{category1}||'%' and category2 like '%'||#{category2}||'%'
	)p ) where R between #{startRow} and #{lastRow}
