select * from(
	select rownum R, p.* from (
		select * from product where category1 like '%'||#{category1}||'%' and category2 like '%'||#{category2}||'%' 


				order by reg_date desc 


				order by name asc 


				order by price asc 


				order by price desc 


				order by count desc 


		)p 

		) where R between #{startRow} and #{lastRow}

